mapper.select:
    	select ctime, c_ext cExt
		from server_stat
		where ip=#{ip} and cdate=#{cdate}

    	select ctime, nin_ext ninExt,nout_ext noutExt
		from server_stat
		where ip=#{ip} and cdate=#{cdate}

    	select ctime, d_ext dExt,dspace
		from server_stat
		where ip=#{ip} and cdate=#{cdate}
